import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';


import { MatDialog, MatDialogRef } from '@angular/material/dialog';
import { AlertdialogComponent } from '../alertdialog/alertdialog.component';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements  OnInit{
  dialogRef!: MatDialogRef<any>;

  taskCreationVisible: boolean = false;
  actionVisible:boolean=false;
  btnvisible:boolean=true;
  todoForm!: FormGroup;
                constructor(private formBuilder: FormBuilder, private dialog: MatDialog)
                {

                }
                ngOnInit() {
                      this.todoForm = this.formBuilder.group({
                        task: ['', Validators.required],
                        dueDate: [''],
                        priority: [''],

                      });
                }
  showTaskCreation() {
    this.taskCreationVisible = true;
    showViewTasks=true;
    this.actionVisible=true;
    this.btnvisible=false;
  }

  get task() {
    return this.todoForm.get('task');
  }

  isFormValid() {
    const formControls = this.todoForm.controls;
    for (const controlName in formControls) {
      if (formControls.hasOwnProperty(controlName) && formControls[controlName].value === '') {
        return false;
      }
    }
    return true;
  }


  openAlert() {
    this.dialogRef = this.dialog.open(AlertdialogComponent, {
      width: '300px', // Customize the width as per your needs
      data: { message: 'Please fill all fields.' } // Pass any additional data to the dialog
    });
  }

  onSubmit(){
    this.openAlert();
    if (!this.isFormValid()) {
      this.openAlert();
      return;
    }
    console.log(this.todoForm.value);
  }

}
